import React, { useState, useEffect } from 'react'
import axios from 'axios'
import NewsCard from '../components/NewsCard'
function Home() {
    const [news, setNews] = useState([])
    const [pendingApiCall, setPendingApiCall] = useState(true)
    const [subject, setSubject] = useState("tr")
    const [language, setLanguage] = useState("tr")
    const date=new Date()

    useEffect(() => {
        axios.get(`https://newsapi.org/v2/top-headlines?country=tr&apiKey=b35395bced044420924bf3b283fcf780`)
            .then((res) => {
                setPendingApiCall(false)
                setNews(res.data)
                console.log(res.data);
            })
    }, [])

    useEffect(() => {
        if (subject == 'world') {
            axios.get(`https://newsapi.org/v2/top-headlines?&language=en&from=${date}&sortBy=publishedAt&apiKey=b35395bced044420924bf3b283fcf780`)
                .then((res) => {
                    setPendingApiCall(false)
                    setNews(res.data)
                    console.log(res.data);
                })
        } else if (subject == 'technology') {
            axios.get(`https://newsapi.org/v2/everything?sources?q=/${subject}&language=${language}&from=${date}&sortBy=publishedAt&apiKey=b35395bced044420924bf3b283fcf780`)
                .then((res) => {
                    setPendingApiCall(false)
                    setNews(res.data)
                    console.log(res.data);
                })


        } else if (subject == 'technology') {
            axios.get(`https://newsapi.org/v2/everything?sources?category=${subject}&language=${language}&from=2024-03-06&sortBy=publishedAt&apiKey=b35395bced044420924bf3b283fcf780`)
                .then((res) => {
                    setPendingApiCall(false)
                    setNews(res.data)
                    console.log(res.data);
                })
        } else if (subject == 'technology') {
            axios.get(`https://newsapi.org/v2/everything?sources?category=${subject}&language=${language}&from=${date}&sortBy=publishedAt&apiKey=b35395bced044420924bf3b283fcf780`)
                .then((res) => {
                    setPendingApiCall(false)
                    setNews(res.data)
                    console.log(res.data);
                })
        }
    }, [subject])

    const handleOnClick = (subject) => {


    }

    return (
        <>
            {pendingApiCall
                ?
                <div class="flex items-center justify-center w-full h-full">
                    <div class="flex justify-center items-center space-x-1 text-sm text-gray-700">

                        <svg fill='none' class="w-6 h-6 animate-spin" viewBox="0 0 32 32" xmlns='http://www.w3.org/2000/svg'>
                            <path clip-rule='evenodd'
                                d='M15.165 8.53a.5.5 0 01-.404.58A7 7 0 1023 16a.5.5 0 011 0 8 8 0 11-9.416-7.874.5.5 0 01.58.404z'
                                fill='currentColor' fill-rule='evenodd' />
                        </svg>


                        <div>Loading ...</div>
                    </div>
                </div>
                : null
            }
            <div className='flex justify-center   items-center  '>
                <ul className='flex gap-8'>
                    <li className='   text-2xl  hover:border-b-4  border-dark-red px-4 py-2 rounded-sm  cursor-pointer'>
                        T端rkiye
                    </li>
                    <li onClick={() => setSubject("world")} className='text-2xl   hover:border-b-4 border-dark-red px-4 py-2 rounded-sm  cursor-pointer'>
                        D端nya
                    </li>

                    <li onClick={() => setSubject("sport")} className='text-2xl  hover:border-b-4 border-dark-red px-4 py-2 rounded-sm  cursor-pointer'>
                        Spor
                    </li>
                    <li onClick={() => setSubject("technology")} className='text-2xl  hover:border-b-4  border-dark-red px-4 py-2 rounded-sm  cursor-pointer'>
                        Teknoloji
                    </li>
                    <li onClick={() => setSubject("science")} className='text-2xl  hover:border-b-4  border-dark-red px-4 py-2 rounded-sm  cursor-pointer'>
                        Bilim
                    </li>
                    <li onClick={() => setSubject("business")} className='text-2xl  hover:border-b-4  border-dark-red px-4 py-2 rounded-sm  cursor-pointer'>
                        Ekonomi
                    </li>

                </ul>
            </div>
            <NewsCard news={news} pendingApiCall={pendingApiCall} />
        </>
    )
}

export default Home
