        params: {lr: 'tr-TR'},
        headers: {
          'X-RapidAPI-Key': '178a257899mshdbb6553358cc760p15e847jsn5eeebd79508b',
          'X-RapidAPI-Host': 'google-news13.p.rapidapi.com'
        }
      };

    useEffect(()=>{
        axios.get("https://newsapi.org/v2/top-headlines?country=tr&apiKey=f440eaf214bf4f48ad71022fcb003e5b")
