import React, { useState, useEffect } from 'react'
import axios from 'axios'
import NewsCard from '../components/NewsCard'
function Home() {
    const [news, setNews] = useState([])
    const [pendingApiCall, setPendingApiCall] = useState(true)
    const [subject, setSubject] = useState("latest")



    const NewsAPI = require('newsapi');
    const newsapi = new NewsAPI('f440eaf214bf4f48ad71022fcb003e5b');


    useEffect(() => {
        // axios.get(`https://google-news13.p.rapidapi.com/${subject}`, {
        //     params: { lr: 'tr-TR' },
        //     headers: {
        //         'X-RapidAPI-Key': '178a257899mshdbb6553358cc760p15e847jsn5eeebd79508b',
        //         'X-RapidAPI-Host': 'google-news13.p.rapidapi.com'
        //     }
        // })
        //     .then((res) => {
        //         setPendingApiCall(false)
        //         setNews(res.data)
        //         console.log(res.data);
        //     })

        newsapi.v2.sources({
            category: 'technology',
            language: 'en',
            country: 'us'
          }).then(response => {
            console.log(response);
            setNews(response.data)
          });
    }, [subject])


    return (
        <>
            {pendingApiCall
                ?
                <div class="flex items-center justify-center w-full h-full">
                    <div class="flex justify-center items-center space-x-1 text-sm text-gray-700">

                        <svg fill='none' class="w-6 h-6 animate-spin" viewBox="0 0 32 32" xmlns='http://www.w3.org/2000/svg'>
                            <path clip-rule='evenodd'
                                d='M15.165 8.53a.5.5 0 01-.404.58A7 7 0 1023 16a.5.5 0 011 0 8 8 0 11-9.416-7.874.5.5 0 01.58.404z'
                                fill='currentColor' fill-rule='evenodd' />
                        </svg>


                        <div>Loading ...</div>
                    </div>
                </div>
                : null
            }
            <div className='flex justify-center   items-center  '>
                <ul className='flex gap-8'>
                    <li onClick={() => setSubject("latest")} className='   text-2xl  hover:border-b-4  border-dark-red px-4 py-2 rounded-sm  cursor-pointer'>
                        Türkiye
                    </li>
                    <li onClick={() => setSubject("world")} className='text-2xl   hover:border-b-4 border-dark-red px-4 py-2 rounded-sm  cursor-pointer'>
                        Dünya
                    </li>

                    <li onClick={() => setSubject("sport")} className='text-2xl  hover:border-b-4 border-dark-red px-4 py-2 rounded-sm  cursor-pointer'>
                        Spor
                    </li>
                    <li onClick={() => setSubject("technology")} className='text-2xl  hover:border-b-4  border-dark-red px-4 py-2 rounded-sm  cursor-pointer'>
                        Teknoloji
                    </li>
                    <li onClick={() => setSubject("science")} className='text-2xl  hover:border-b-4  border-dark-red px-4 py-2 rounded-sm  cursor-pointer'>
                        Bilim
                    </li>
                    <li onClick={() => setSubject("health")} className='text-2xl  hover:border-b-4  border-dark-red px-4 py-2 rounded-sm  cursor-pointer'>
                        Sağlık
                    </li>

                </ul>
            </div>
            <NewsCard news={news} pendingApiCall={pendingApiCall} />
        </>
    )
}

export default Home
